std::vector<short> polacz_wyniki_filtrow(std::vector<short> v1, std::vector<short> v2)
{
    std::vector<short> wynik;

    for (const auto& p1 : v1) 
    {
        for (const auto& p2 : v2) 
        {
            if (p1 == p2) // porównanie wskaŸników, czyli ten sam obiekt
            { 
                wynik.push_back(p1);
                break; // wystarczy jeden raz
            }
        }
    }
    return wynik;
}

void Gosc::gui() {
    
    system("cls");
    //system("clear");
    int a;
    do {
        std::cout << "1. Pokaz katalog.\n2. Pokaz historie rezerwacji.\n3. Wyloguj sie.\n";
        std::cin >> a;
        if (std::cin.fail()) {
            std::cin.clear();
            std::cin.ignore(1000, '\n');
        }
        if (a == 1) {
            przegladaj_katalog();
        }
        if (a == 2) {
            przegladaj_historie_rezerwacji();
        }
        if (a == 3) {
            wyloguj();
            break;
        }
    } while (true);
}

