#include "Pracownik.h"

Pracownik::Pracownik(){}

void Pracownik::aktualizuj_katalog() 
{
	auto katalog = std::dynamic_pointer_cast<KatalogDlaPracownikow>(Katalog::pobierzInstancje());
    katalog->dodaj_pokoj(); //tymczasowe
    katalog->dodaj_usluge(); //tymczasowe
}

void Pracownik::przegladaj_katalog() 
{
    auto katalog = std::dynamic_pointer_cast<KatalogDlaPracownikow>(Katalog::pobierzInstancje());
}

void Pracownik::gui() {
	system("cls");
	//system("clear");
    int a;
    do {
        std::cout << "1. Pokaz katalog.\n2. Aktualizuj katalog.\n3. Wyslij wiadomosc.\n4. Zobacz wiadomosci.\n5. Wyloguj sie.\n";
        std::cin >> a;
        if (std::cin.fail()) {
            std::cin.clear();
            std::cin.ignore(1000, '\n');
        }
        if (a == 1) {
            przegladaj_katalog();
        }
        if (a == 2) {
            aktualizuj_katalog();
        }
        if (a == 3) {
            wyslij_wiadomosc();
        }
        if (a == 4) {
            przegladaj_wiadomosci();
        }
        if (a == 5) {
            wyloguj();
            break;
        }
    } while (true);
}

